package com.i.control;

public class Control4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int select = 1;
		switch(select) {
		case 1:
			System.out.println("1 일때 진행");
			break;
		case 2:
			System.out.println("2 일때 진행");
			break;
		default:
			System.out.println("그외 나머지 진행");
		}
	}

}
